mapper.select:
SELECT COUNT(*)
FROM user_behavior
WHERE userId = #{userId}
  AND productId = #{productId}
  AND behaviorTypeId = #{behaviorTypeId}

mapper.update:
UPDATE user_behavior
SET
    behaviorTime = #{behaviorTime},
    isDelete = #{isDelete}
WHERE
    userId = #{userId}
  AND productId = #{productId}
  AND behaviorTypeId = #{behaviorTypeId}

mapper.select:
SELECT *
FROM user_behavior
WHERE userId = #{userId}
  AND productId = #{productId}
  AND behaviorTypeId = #{behaviorTypeId}
    LIMIT 1

SELECT COUNT(*)
FROM cart
WHERE userId = #{userId}
  AND productId = #{productId}

SELECT COUNT(*)
FROM user_behavior
WHERE userId = #{userId}
  AND productId = #{productId}
  AND behaviorTypeId = #{behaviorTypeId}

mapper.update:
UPDATE cart
SET count = count + #{count}
WHERE userId = #{userId}
  AND productId = #{productId}

UPDATE user_behavior
SET behaviorTime = #{behaviorTime},
    isDelete = #{isDelete}
WHERE userId = #{userId}
  AND productId = #{productId}

UPDATE cart
SET count = #{count}
WHERE userId = #{userId}
  AND productId = #{productId}
